mapper.select:
SELECT v.id id, v.author_id authorId, u.user_name authorName, u.user_avatar authorAvatar, v.create_time createTime, v.url url, v.cover cover, v.description description, v.likes likes, v.duration duration, v.downloads downloads, v.comments comments from video v LEFT JOIN user_info u on u.user_id = v.author_id
author_id = #{userInfo.userId}
order by create_time desc

mapper.update:
update video

    likes = #{video.likes},
    comments = #{video.comments},
    downloads = #{video.downloads},
    description = #{video.description}

where id = #{video.id}

mapper.insert:
insert into video (author_id, create_time, url, cover, description, likes, downloads, comments)
VALUES (#{video.authorId}, #{video.createTime}, #{video.url}, #{video.cover}, #{video.description},
#{video.likes}, #{video.downloads}, #{video.comments});
